let indicatorEventDetailCount = CitrixAnalytics_indicatorEventDetails_CL | count; 
let indicatorSummaryCount = CitrixAnalytics_indicatorSummary_CL | count ;
let riskScoreChangeCount = CitrixAnalytics_riskScoreChange_CL | count ;
print toscalar(userProfileCount)
       +toscalar(indicatorEventDetailCount)
       +toscalar ( indicatorSummaryCount)
       +toscalar ( userProfileCount)
       +toscalar ( riskScoreChangeCount);

